package InterviewQuestions;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
	    int res = 1;
	    int i; 
	        for (i=2; i<=n; i++) 
	            {
	        		res *= i; 
	            }
	      System.out.println(res);
	} 
	}


